mapper.select:
select count(*)
from member
	 join department using(d_no)
where m_status = 'Y'

		and d_no = #{selectValue}


		and job_grade = #{selectValue}

select m_no, email, name, job_grade, phone, d_no, d_name, m_hired, p_url from member join department using(d_no) left join profile using(m_no) where m_status = 'Y'
and d_no = #{selectValue}
and job_grade = #{selectValue}
order by m_no
